mapper.select:
SELECT value FROM attr
WHERE 1
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}

SELECT * 
FROM attr
WHERE 1
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}

SELECT * 
FROM attr
WHERE 1
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}
AND value = #{value}

mapper.update:
UPDATE attr
SET updateDate = NOW(),
value = #{value}
WHERE 1
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}

mapper.select:
SELECT * FROM attr
WHERE 1
AND relTypeCode = #{relTypeCode}
